import React,{Component} from 'react';

import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from 'axios';
import { Redirect } from 'react-router';
import './addRooms.css';




class AddRoomsPage extends Component {


  constructor(props){
    super(props);
    this.state = {
         room_num : 0,
         floor_num : 0
    }
    this.submitAddRooms = this.submitAddRooms.bind(this);
    this.detailsChange = this.detailsChange.bind(this);
  }


  // getCookie(cName) {
  //   const name = cName + "=";
  //   const cDecoded = decodeURIComponent(document.cookie); //to be careful
  //   const cArr = cDecoded .split('; ');
  //   let res;
  //   cArr.forEach(val => {
  //       if (val.indexOf(name) === 0) res = val.substring(name.length);
  //   })
  //   return res;
  // }

  submitAddRooms(event){
      console.log(this.state);
      event.preventDefault();
      // const token = this.getCookie('admin_cookie');
      // const headers = { 
      //     'Authorization': `Bearer ${token}` 
      // };



      axios.post('http://localhost:8095/add-room', this.state)
      .then(response => 
        {
          if(response.status==200){
            alert("Room Added!");
          }
          else{
            alert("Room not added.Please Try Again");
          }
          }
      );
  }

  detailsChange(event){
      this.setState({
          [event.target.name]:event.target.value
      });
  }

  headers = {
      "Content-Type": "application/json"
  };


  render(){

    //if(!this.state.isLoggedIn){
      return (
        <div className="AddRoomsPage">
          <h1>Add Rooms</h1>
          <Form onSubmit={this.submitAddRooms}>
            <Form.Group size="lg" className="mb-3" controlId="formBasicRoomNumber">
              <Form.Label>Room Number</Form.Label>
              <Form.Control
                required type="text"
                value={this.state.room_num}
                name = "room_num"
                onChange={this.detailsChange}
                placeholder = "123"
              />
            </Form.Group>
            <Form.Group size="lg" className="mb-3" controlId="formBasicFloorNumber">
              <Form.Label>Floor Number</Form.Label>
              <Form.Control
                required type="text"
                value={this.state.floor_num}
                onChange={this.detailsChange}
                placeholder="7"
                name="floor_num"
              />
            </Form.Group>
            <Button size="lg" type="submit">
              Add room
            </Button>
          </Form>

        </div>

      );
    // }
    // else{
    //   return <Redirect to = {{ pathname: "/" }} />;
    // }

  }
}

export default AddRoomsPage;
