import React,{Component} from 'react';

import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from 'axios';
import { Redirect } from 'react-router';
import './addRooms.css';




class AddTestResultPage extends Component {


  constructor(props){
    super(props);
    this.state = {
        student_id : '',
        result : ''
    }
    this.submitAddTestResult = this.submitAddTestResult.bind(this);
    this.detailsChange = this.detailsChange.bind(this);
  }


  // getCookie(cName) {
  //   const name = cName + "=";
  //   const cDecoded = decodeURIComponent(document.cookie); //to be careful
  //   const cArr = cDecoded .split('; ');
  //   let res;
  //   cArr.forEach(val => {
  //       if (val.indexOf(name) === 0) res = val.substring(name.length);
  //   })
  //   return res;
  // }

  submitAddTestResult(event){
      console.log(this.state);
      event.preventDefault();
      // const token = this.getCookie('admin_cookie');
      // const headers = { 
      //     'Authorization': `Bearer ${token}` 
      // };



      axios.post('http://localhost:8095/add-test', this.state)
      .then(response => 
        {
          if(response.status==200){
            alert("Result Added!");
          }
          else{
            alert("Result not added.Please Try Again");
          }
          }
      )
      .catch(err=>{
        console.log(err);
      });
  }

  detailsChange(event){
      this.setState({
          [event.target.name]:event.target.value
      });
  }

  headers = {
      "Content-Type": "application/json"
  };


  render(){

    //if(!this.state.isLoggedIn){
      return (
        <div className="AddTestResultPage">
          <h1>Add Test Result</h1>
          <Form onSubmit={this.submitAddTestResult}>
            <Form.Group size="lg" className="mb-3" controlId="formBasicStudentId">
              <Form.Label>Student ID</Form.Label>
              <Form.Control
                required type="text"
                value={this.state.student_id}
                name = "student_id"
                onChange={this.detailsChange}
                placeholder = "123"
              />
            </Form.Group>
            <Form.Group size="lg" className="mb-3" controlId="formBasicResult">
              <Form.Label>Result</Form.Label>
              <Form.Control
                required type="text"
                value={this.state.result}
                onChange={this.detailsChange}
                placeholder="+VE/-VE"
                name="result"
              />
            </Form.Group>
            <Button size="lg" type="submit">
              Add Test Result
            </Button>
          </Form>

        </div>

      );
    // }
    // else{
    //   return <Redirect to = {{ pathname: "/" }} />;
    // }

  }
}

export default AddTestResultPage;
