              <tr>
                <th>Student ID</th>
                <th>Result</th>
                <th>Test Date</th>
                <th>Allocate</th>
              </tr>
            </MDBTableHead>
            <MDBTableBody>
              {
                this.state.users.map((obj)=>(
                 <tr>
                    <td>{obj.student_id}</td>
                    <td>{obj.result}</td>
                    <td>{obj.testDate}</td>
                    <td>
                      <Button className = "buttonsize" size="lg" type="button"
                          onClick={(e) => {

                              e.preventDefault();
                              this.setState({room_id:obj.room_id})
                              this.getData(obj.room_id)  ;

                          }}
                      >
                          Allocate Room
                      </Button>

                    </td>


                </tr>   		


