import React, { useEffect, useState,useRef, useLayoutEffect } from "react";
import "./navbar.css";


import { NavLink } from "react-router-dom";



const Navbar = () => {

  let isLoggedIn = false;

  function getCookie(name) {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
  }




  let cookie;

  const [showMediaIcons, setShowMediaIcons] = useState(false);
  const isInitialMount = useRef(true);
  useEffect(() =>{

    if (isInitialMount.current) {
      const cookie = getCookie("admin_cookie");
      if(cookie!=undefined){
        isLoggedIn = true;
      }
      else{
        isLoggedIn = false;
      }
      isInitialMount.current = false;
    }
    else{
      const cookie = getCookie("admin_cookie");
      if(cookie!=undefined){
        isLoggedIn = true;
      }
      else{
        isLoggedIn = false;
      }
    }


  });


  console.log(isLoggedIn);
  if(isLoggedIn){
    return (
      <>
        <nav className="main-nav">
          {/* 1st logo part  */}
          <div className="logo">
            <h2>
              <span>C</span>ovid
              <span>Q</span>UARANTINE
            </h2>
          </div>

          {/* 2nd menu part  */}
          <div
            className={
              showMediaIcons ? "menu-link mobile-menu-link" : "menu-link"
            }>
            <ul>
              <li>
                <NavLink to="/">Home</NavLink>
              </li>

              <li>
                <NavLink to="/add-members">Add Member</NavLink>
              </li>
              <li>
                <NavLink to="/add-rooms">Add Rooms</NavLink>
              </li>
              <li>
                <NavLink to="/add-tests">Add Test Results</NavLink>
              </li>
              <li>
                <NavLink to="/free-room">Room Allocation</NavLink>
              </li>
              <li>
                <NavLink to="/deallocate-rooms">Room Deallocation</NavLink>
              </li>
              <li>
                <NavLink to="/positive-students">Positive Students</NavLink>
              </li>
              <li>
                <NavLink to="/logout">Logout</NavLink>
              </li>

            </ul>
          </div>


        </nav>
      </>
    );

  }
  else{
    cookie = getCookie("admin_cookie"); 
    if(cookie!=null){
      isLoggedIn = true;
      //alert("cookie there");
    }
    else{
      isLoggedIn = false;
    } 
    console.log("Inside else case");
    console.log(cookie);
    //setShowMediaIcons(true);
    for(var i=0;i<10000;i++){

    }
    return (
      <>
        <nav className="main-nav">
          {/* 1st logo part  */}
          <div className="logo">
            <h2>
              <span>C</span>ovid
              <span>Q</span>UARANTINE
            </h2>
          </div>

          {/* 2nd menu part  */}
          <div
            className={
              showMediaIcons ? "menu-link mobile-menu-link" : "menu-link"
            }>
            <ul>

              <li>
                <NavLink to="/login-admin">Admin Login</NavLink>
              </li>
            </ul>
          </div>


        </nav>
      </>
    );

  }
};

export default Navbar;
